package com.syntax.reviewclass2;

public class OperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	//operators in java +, -, /, *, %
	// logical operators ==, !=, >, <, <=, >=, 
		
		int num=10;
		int num2=10;
		System.out.println(num+num2);
		System.out.println(num>num2); //num>num2=>f or num=num2=>t  f+t=t
		
		//whole numbers - 0-infinity , negative infinity to positive infinity
		
		System.out.println(Long.MAX_VALUE); //gives us the maximum range
		System.out.println(Long.MIN_VALUE); //gives us the maximum range
		System.out.println(Byte.MAX_VALUE);
		System.out.println(Integer.MAX_VALUE);
		System.out.println(num/num2);
		System.out.println(num%num2);
		
		System.out.println(10/3);
		System.out.println(10%3);
		
		System.out.println(10.0/3.0); //=3.333333333335
		System.out.println((int)10.0/3); //=3
		System.out.println(10.0/3);


		
		

		
		
		
		
		
		
		
		
		
		
		
		
	}

}
